    private void SaveMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SaveMouseClicked
        String tampilan ="yyyy-MM-dd";
        SimpleDateFormat fm = new SimpleDateFormat(tampilan);
        String tanggal = String.valueOf(fm.format(tgl_lahir.getDate()));
        String sql = "insert into data_dokter values(?,?,?,?,?,?,?)";
        try {
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, id_dokter.getText());
            stat.setString(2, nama.getText());
            stat.setString(3, jenkel.getSelection().getActionCommand());
            stat.setString(4, tanggal);
            stat.setString(5, email.getText());
            stat.setString(6, no_telpon.getText());
            stat.setString(7, alamat.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            id_dokter.requestFocus();
            datatable();
            kosong();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan "+e);
        }        // TODO add your handling code here:
    }//GEN-LAST:event_SaveMouseClicked

    private void EditMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_EditMouseClicked
        String tampilan ="yyyy-MM-dd";
        SimpleDateFormat fm = new SimpleDateFormat(tampilan);
        String tanggal = String.valueOf(fm.format(tgl_lahir.getDate()));
        String query;
        query = "UPDATE data_dokter SET nama = ?, jenkel = ?, tgl_lahir = ?, email = ?, no_telepon = ?, alamat = ? WHERE id_dokter ='"+id_dokter.getText()+"'";
        PreparedStatement statement;
        Connection connection;
        try{
            connection = koneksi.connect();
            statement = connection.prepareStatement(query);
            statement.setString(1, nama.getText());
            statement.setString(2, jenkel.getSelection().getActionCommand());
            statement.setString(3, tanggal);
            statement.setString(4, email.getText());
            statement.setString(5, no_telpon.getText());
            statement.setString(6, alamat.getText());
            

            int hasil = statement.executeUpdate();
            if (hasil==1) {
                JOptionPane.showMessageDialog(this, "DATA SUKSES DIUBAH");
                id_dokter.requestFocus();
                datatable();
                kosong();
            }
        }catch(SQLException e) {
            JOptionPane.showInternalMessageDialog(this, e.getMessage());
        }        // TODO add your handling code here:
    }//GEN-LAST:event_EditMouseClicked

