    private void DeleteMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_DeleteMouseClicked
        int ok = JOptionPane.showConfirmDialog(null, "Apakah Anda yakin ingin menghapus data", "Konfirmasi Dialog", JOptionPane.YES_NO_OPTION);
        if(ok==0){
            String sql = "delete from data_dokter where id_dokter='"+id_dokter.getText()+"'";
            try {
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
                id_dokter.requestFocus();
                datatable();
            } catch (SQLException e) {
                JOptionPane.showMessageDialog(null, "Data Gagal Dihapus "+e);
            }
        }         // TODO add your handling code here:
    }//GEN-LAST:event_DeleteMouseClicked

    private void dokterMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_dokterMouseClicked
        int i = dokter.getSelectedRow();
   TableModel model = dokter.getModel();
   id_dokter.setText(model.getValueAt(i,0).toString());
   nama.setText(model.getValueAt(i,1).toString());
   email.setText(model.getValueAt(i,4).toString());
   no_telpon.setText(model.getValueAt(i,5).toString());
   alamat.setText(model.getValueAt(i,6).toString()); 

    }//GEN-LAST:event_dokterMouseClicked

