    protected void datatable(){
    Object [] Baris = {"Kode Barang","Nama Barang","Jenis Barang","Jumlah","Harga","Expired","Keterangan"};
        tabmode = new DefaultTableModel(null, Baris);
        barang.setModel(tabmode);
        String sql = "select * from data_barang";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
            String a = hasil.getString("kode_barang");
            String s = hasil.getString("nama_barang");
            String d = hasil.getString("jenis_barang");
            String f = hasil.getString("jumlah");
            String g = hasil.getString("harga");
            String h = hasil.getString("expired");
            String i = hasil.getString("keterangan");
            
            String[] data={a,s,d,f,g,h,i};
            tabmode.addRow(data);
        }
    } catch (Exception e) {
        
    }
}


