    private void jLabel3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel3MouseClicked

try {
           File file = new File("src/Report/laporan_data_barang.jrxml");
            jasperDesign = JRXmlLoader.load(file);
            param.clear();
            jasperReport = JasperCompileManager.compileReport(jasperDesign);
            jasperPrint = JasperFillManager.fillReport(jasperReport, param,    koneksi.connect());
            JasperViewer.viewReport(jasperPrint, false);
        } catch (Exception e) {
            e.printStackTrace();
        }         // TODO add your handling code here:
    }//GEN-LAST:event_jLabel3MouseClicked

    private void sActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sActionPerformed
        try
        {
            Statement st = conn.createStatement();
            String search = Search2.getText();
            ResultSet rs = st.executeQuery("SELECT * FROM data_barang WHERE kode_barang like '%"+search+"%' OR nama_barang like '%"+search+"%'");
            DefaultTableModel dtm = (DefaultTableModel) barang.getModel();

            dtm.setRowCount(0);
            String [] data = new String[8];
            int i = 1;

            while(rs.next())
            {

            data[0] = rs.getString("kode_barang");
            data[1] = rs.getString("nama_barang");
            data[2] = rs.getString("jenis_barang");
            data[3] = rs.getString("jumlah");
            data[4] = rs.getString("harga");
            data[5] = rs.getString("expired");
            data[6] = rs.getString("keterangan");
                dtm.addRow(data);
                i++;
            }
            rs.close();
            Search2.setText("");
        }
        catch(Exception ex)  {
            JOptionPane.showMessageDialog(null,"Data yang Anda cari Tidak dapat Ditemukan");
            System.err.println("error (search) : " +ex);
        }    // TODO add your handling code here:        // TODO add your handling code here:
    }//GEN-LAST:event_sActionPerformed

